package com.spoon.loststations.vo;

public class CommentData {
	private int userNo;
	private int errorAdoptNum;
	private int totalComment;
	private int adoptNum;
	
	
	
	
	public CommentData() {
		// TODO Auto-generated constructor stub
	}
	
	public int getUserNo() {
		return userNo;
	}
	public void setUserNo(int userNo) {
		this.userNo = userNo;
	}
	
	public int getErrorAdoptNum() {
		return errorAdoptNum;
	}

	public void setErrorAdoptNum(int errorAdoptNum) {
		this.errorAdoptNum = errorAdoptNum;
	}

	public int getTotalComment() {
		return totalComment;
	}
	public void setTotalComment(int totalComment) {
		this.totalComment = totalComment;
	}
	public int getAdoptNum() {
		return adoptNum;
	}
	public void setAdoptNum(int adoptNum) {
		this.adoptNum = adoptNum;
	}
	
}
